{
  "_comment": "Pi Chronicle project settings. Agents read this to understand your project conventions.",

  "project": {
    "name": "AdventureEngine",
    "description": "",
    "language": "TypeScript",
    "framework": "",
    "runtime": "Node.js"
  },

  "architecture": {
    "pattern": "clean",
    "notes": "Clean Architecture: domain ‚Üí application ‚Üí infrastructure ‚Üí presentation. Dependencies point inward only."
  },

  "development": {
    "tdd": false,
    "styleGuide": "airbnb",
    "linter": "eslint",
    "formatter": "prettier"
  },

  "testing": {
    "framework": "vitest",
    "e2e": "",
    "coverageThreshold": 80,
    "testFilePattern": "**/*.test.ts"
  },

  "git": {
    "commitStyle": "conventional",
    "branchStrategy": "trunk",
    "defaultBranch": "main",
    "requirePR": false
  },

  "sprint": {
    "length_days": 10,
    "_comment_capacity": "Total story points the team can deliver per sprint. Default 8 = ~4 medium items or 8 small items.",
    "capacity_points": 8,
    "_comment_buffer": "Reserve this fraction of capacity for interruptions, bugs, and review. 0.2 = 20% buffer.",
    "buffer": 0.2,
    "_comment_scale": "Point scale: small=1, medium=2, large=3. Never plan xl (5pt) items ‚Äî break them down first.",
    "point_scale": { "small": 1, "medium": 2, "large": 3, "xl": 5 }
  },

  "skills": {
    "always": ["git"],
    "auto": true,
    "_comment_auto": "When true, auto-loads skills based on settings above (tdd, testing.framework, git.commitStyle, architecture.pattern)"
  }
}
